import React, { useState, useEffect, useRef } from 'react';
import './HeroSlider.css';

const HeroSlider = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [loadedImages, setLoadedImages] = useState(0);
  const sliderRef = useRef(null);

  const slides = [
    {
      id: 1,
      image: '/11.png',
      title: 'Duygusal Yolculuk',
      subtitle: 'Akrilik & Karma Teknik',
      description: 'İç dünyanın renklerle buluştuğu anlar'
    },
    {
      id: 2,
      image: '/7.png',
      title: 'Renk Harmanı',
      subtitle: 'Yağlı Boya',
      description: 'Her fırça darbesi bir hikaye anlatır'
    },
    {
      id: 3,
      image: '/13.png',
      title: 'Soyut Keşif',
      subtitle: 'Karma Teknik',
      description: 'Form ve renklerin dansı'
    }
  ];

  useEffect(() => {
    // Simulate image loading
    const loadImages = () => {
      let loaded = 0;
      slides.forEach((slide, index) => {
        const img = new Image();
        img.onload = () => {
          loaded++;
          setLoadedImages(loaded);
          if (loaded === slides.length) {
            setIsLoading(false);
          }
        };
        img.src = slide.image;
      });
    };

    loadImages();
  }, []);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  if (isLoading) {
    return (
      <div className="loader">
        <span className="loader-text">{Math.round((loadedImages / slides.length) * 100)}%</span>
      </div>
    );
  }

  return (
    <div className="slider" ref={sliderRef}>
      <button className="slider--btn slider--btn__prev" onClick={prevSlide}>
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
          <path d="m15 18-6-6 6-6"/>
        </svg>
      </button>

      <div className="slides__wrapper">
        <div className="slides">
          {slides.map((slide, index) => {
            const isCurrent = index === currentSlide;
            const isNext = index === (currentSlide + 1) % slides.length;
            const isPrev = index === (currentSlide - 1 + slides.length) % slides.length;
            
            return (
              <div
                key={slide.id}
                className="slide"
                data-current={isCurrent}
                data-next={isNext}
                data-previous={isPrev}
              >
                <div className="slide__inner">
                  <div className="slide--image__wrapper">
                    <img className="slide--image" src={slide.image} alt={slide.title} />
                  </div>
                </div>
              </div>
            );
          })}
        </div>

        <div className="slides--infos">
          {slides.map((slide, index) => {
            const isCurrent = index === currentSlide;
            const isNext = index === (currentSlide + 1) % slides.length;
            const isPrev = index === (currentSlide - 1 + slides.length) % slides.length;
            
            return (
              <div
                key={slide.id}
                className="slide-info"
                data-current={isCurrent}
                data-next={isNext}
                data-previous={isPrev}
              >
                <div className="slide-info__inner">
                  <div className="slide-info--text__wrapper">
                    <div data-title className="slide-info--text">
                      <span>{slide.title}</span>
                    </div>
                    <div data-subtitle className="slide-info--text">
                      <span>{slide.subtitle}</span>
                    </div>
                    <div data-description className="slide-info--text">
                      <span>{slide.description}</span>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>

      <button className="slider--btn slider--btn__next" onClick={nextSlide}>
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
          <path d="m9 18 6-6-6-6"/>
        </svg>
      </button>
    </div>
  );
};

export default HeroSlider;
