import React, { useEffect, useState } from 'react';
import Navbar from './components/Navbar';
import HeroSlider from './components/HeroSlider';
import ArtistIntro from './components/ArtistIntro';
import AutoGallery from './components/AutoGallery';
import Chatbot from './components/Chatbot';
import ArtworksPage from './components/ArtworksPage';
import ArtworkDetail from './components/ArtworkDetail';
import Footer from './components/Footer';
import './App.css';

function App() {
  const [currentPage, setCurrentPage] = useState('home');
  const [artworkId, setArtworkId] = useState(null);

  useEffect(() => {
    // Set background image dynamically
    document.documentElement.style.backgroundImage = "url('/bg.jpg')";
    document.documentElement.style.backgroundSize = "cover";
    document.documentElement.style.backgroundPosition = "center center";
    document.documentElement.style.backgroundRepeat = "no-repeat";
    document.documentElement.style.backgroundAttachment = "fixed";

    // Sayfa yüklendiğinde ve yenilendiğinde en üstten başla
    window.scrollTo(0, 0);

    // Sayfa yenilendiğinde de scroll pozisyonunu sıfırla
    window.addEventListener('beforeunload', () => {
      window.scrollTo(0, 0);
    });

    // Sayfa tamamen yüklendiğinde de kontrol et
    window.addEventListener('load', () => {
      window.scrollTo(0, 0);
    });

    // URL değişikliklerini dinle
    const handleRouteChange = () => {
      const path = window.location.pathname;

      if (path === '/eserler') {
        setCurrentPage('artworks');
        setArtworkId(null);
      } else if (path.startsWith('/eser/')) {
        const id = path.split('/eser/')[1];
        setCurrentPage('artwork-detail');
        setArtworkId(id);
      } else {
        setCurrentPage('home');
        setArtworkId(null);
      }
    };

    // İlk yükleme
    handleRouteChange();

    // Popstate event listener (geri/ileri butonları için)
    window.addEventListener('popstate', handleRouteChange);

    return () => {
      window.removeEventListener('popstate', handleRouteChange);
    };
  }, []);

  const renderPage = () => {
    switch (currentPage) {
      case 'artworks':
        return <ArtworksPage />;
      case 'artwork-detail':
        return <ArtworkDetail artworkId={artworkId} />;
      default:
        return (
          <>
            <HeroSlider />
            <ArtistIntro />
            <Chatbot />
            <AutoGallery />
          </>
        );
    }
  };

  return (
    <div className="App">
      <Navbar />
      <main>
        {renderPage()}
      </main>
      <Footer />
    </div>
  );
}

export default App;
