import React from 'react';
import { motion } from 'framer-motion';
import './AutoGallery.css';

const AutoGallery = () => {
  const artworks = [
    { id: 1, image: '/assets/11.png', alt: 'Eser 1' },
    { id: 2, image: '/assets/7.png', alt: 'Eser 2' },
    { id: 3, image: '/assets/13.png', alt: 'Eser 3' },
    { id: 4, image: '/assets/11.png', alt: 'Eser 1' },
    { id: 5, image: '/assets/7.png', alt: 'Eser 2' },
    { id: 6, image: '/assets/13.png', alt: 'Eser 3' }
  ];

  return (
    <motion.section 
      className="gallery-section"
      initial={{ opacity: 0 }}
      whileInView={{ opacity: 1 }}
      transition={{ duration: 0.8 }}
      viewport={{ once: true }}
    >
      <motion.div 
        className="gallery-title"
        initial={{ opacity: 0, y: 30 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.2, duration: 0.6 }}
        viewport={{ once: true }}
      >
        <h2>Seçili Eserler</h2>
        <p>Son çalışmalarımdan bir seçki</p>
      </motion.div>

      <div className="gallery-track">
        {artworks.map((artwork, index) => (
          <motion.div
            key={artwork.id}
            className="gallery-item"
            initial={{ opacity: 0, scale: 0.8 }}
            whileInView={{ opacity: 1, scale: 1 }}
            transition={{ delay: index * 0.1, duration: 0.5 }}
            viewport={{ once: true }}
            whileHover={{ 
              y: -8, 
              scale: 1.02,
              transition: { duration: 0.3 }
            }}
          >
            <img src={artwork.image} alt={artwork.alt} />
          </motion.div>
        ))}
      </div>
    </motion.section>
  );
};

export default AutoGallery;
